library(shiny)
library(ggplot2)

ui <- fluidPage(
  titlePanel("Random Data Visualization"),
  sidebarLayout(
    sidebarPanel(
      sliderInput("points", "Number of Points:", 10, 500, 100),
      selectInput("color", "Point Color:", c("Blue", "Red", "Green"))
    ),
    mainPanel(
      plotOutput("randomPlot")
    )
  )
)

server <- function(input, output) {
  output$randomPlot <- renderPlot({
    set.seed(123)
    df <- data.frame(x = rnorm(input$points), y = rnorm(input$points))
    ggplot(df, aes(x, y)) +
      geom_point(color = tolower(input$color), alpha = 0.7) +
      theme_minimal()
  })
}

shinyApp(ui, server)
